    (1, 2, 'Values', 'Have awesome values and stick to them', 'https://techcrunch.com/wp-content/uploads/2018/06/IMG_0729-2.jpg', '{"How to Uncover Your Values", "Think about people you most admire", "Think about the things that are most important to you", "Goldilocks rule- pick just the right ones", "Internalizing your values"}', TRUE),
    (1, 3, 'Action', 'Take action to turn your values into reality', 'https://techcrunch.com/wp-content/uploads/2018/06/IMG_0729-2.jpg', '{"Now That You Have Your Values, Take Action", "Practice following your values", "Take it one day, one opportunity at a time", "Evaluate every missed opportunity", "Reflect on when you do follow through", "Build over time"}', TRUE),
    (1, 4, 'Flexibility', 'Nothing is black and white. Learn how and when to take action', 'https://techcrunch.com/wp-content/uploads/2018/06/IMG_0729-2.jpg', '{"Learn How and When to Take Action", "You don''t have to fight every battle", "You don''t have to win every battle you fight", "Pick your battles", "Balance is key"}', TRUE),
    (2, 1, 'Fake It Til You Make It', 'Nobody knows exactly what they''re doing. That means you can do it too', 'https://techcrunch.com/wp-content/uploads/2018/06/IMG_0729-2.jpg', '{"Nobody Knows Exactly What They''re Doing. That Means You Can Do It Too", "You''re allowed to make it up as you go", "Pretend to be confident and you will be", "Studies show that when you smile more you feel happier"}', FALSE),
    (2, 2, 'Power Posing and More', 'Use simple psychological tricks to become a more confident leader', 'https://techcrunch.com/wp-content/uploads/2018/06/IMG_0729-2.jpg', '{"Use These Tricks Tricks to Become a More Confident Leader", "Pose confidently and you''ll feel more confident", "Visualize your success", "Listen to your favorite uplifting music", "When giving a speech, look out into the distance", "Practice makes perfect"}', FALSE),
